#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/11/20# @Author : cyq# @File : interfaceStep# @Software: PyCharm# @Desc:from fastapi import APIRouterfrom app.response import Responsefrom app.schema.interface import AddInterfaceStepSchemafrom app.mapper.interface import InterfaceMapperfrom utils import MyLoguruLOG = MyLoguru().get_logger()router = APIRouter(prefix="/interface/step", tags=['自动化接口步骤'])@router.post("/newStep", description="添加步骤")async def newStep(stepInfo: AddInterfaceStepSchema):    LOG.debug(stepInfo.dict())    await InterfaceMapper.save(**stepInfo.dict())    return Response.success()