#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/11/20# @Author : cyq# @File : interfaceTask# @Software: PyCharm# @Desc:from fastapi import APIRouter, Dependsfrom app.mapper.interface import InterfaceTaskMapperfrom app.controller import Authenticationfrom app.model.base import Userfrom app.response import Responsefrom app.schema.interface import InsertInterfaceCaseTaskSchema, PageInterfaceCaseTaskSchema, \    OptionInterfaceCaseTaskSchema, AssocCasesSchema, RemoveAssocCasesSchemarouter = APIRouter(prefix="/interface/task", tags=['自动化接口步骤'])@router.post('/insertTask', description="创建任务")async def insertTask(taskInfo: InsertInterfaceCaseTaskSchema, creator: User = Depends(Authentication())):    task = await InterfaceTaskMapper.save(        creator=creator,        **taskInfo.dict()    )    return Response.success(task)@router.post("/updateTask", description="修改")async def updateTask(update: OptionInterfaceCaseTaskSchema, updater: User = Depends(Authentication())):    await InterfaceTaskMapper.update_by_id(        updater=updater,        **update.dict(exclude_unset=True,                      exclude_none=True)    )    return Response.success()@router.get("/detail", description="任务详情")async def get_task_detail(task: OptionInterfaceCaseTaskSchema, _: User = Depends(Authentication())):    task = await InterfaceTaskMapper.get_by_id(ident=task.id)    return Response.success(task)@router.post("/removeTask", description='删除任务')async def removeTask(task: OptionInterfaceCaseTaskSchema, _: User = Depends(Authentication())):    await InterfaceTaskMapper.delete_by_id(ident=task.id)    return Response.success()@router.post("/page", description="任务分页")async def pageTask(pageInfo: PageInterfaceCaseTaskSchema, _=Depends(Authentication())):    tasks = await InterfaceTaskMapper.page_query(**pageInfo.dict(        exclude_unset=True,        exclude_none=True    ))    return Response.success(tasks)@router.post("/association/cases", description="关联用例")async def association_cases(info: AssocCasesSchema, _: User = Depends(Authentication())):    await InterfaceTaskMapper.association_cases(**info.dict())    return Response.success()@router.post("/remove/association", description="移除关联")async def association_case(info: RemoveAssocCasesSchema, _: User = Depends(Authentication())):    await InterfaceTaskMapper.remove_association_case(**info.dict())    return Response.success()@router.get("/query/cases", description="查询关联用例")async def query_cases(info: OptionInterfaceCaseTaskSchema = Depends(), _: User = Depends(Authentication())):    cases = await InterfaceTaskMapper.query_case(**info.dict())    return Response.success(cases)@router.post("/reorder/cases", description="重排用例")async def query_cases(info: AssocCasesSchema, _: User = Depends(Authentication())):    cases = await InterfaceTaskMapper.reorder_cases(**info.dict())    return Response.success(cases)