#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/11/20# @Author : cyq# @File : interfaceTask# @Software: PyCharm# @Desc:from fastapi import APIRouter, Dependsfrom app.mapper.interface import InterfaceTaskMapper, InterfaceTaskResultMapperfrom app.controller import Authenticationfrom app.model.base import Userfrom app.response import Responsefrom app.schema.interface import InsertInterfaceCaseTaskSchema, PageInterfaceCaseTaskSchema, \    OptionInterfaceCaseTaskSchema, AssocCasesSchema, RemoveAssocCasesSchema, AssocApisSchema, RemoveAssocApisSchema, \    GetByTaskId, PageInterfaceTaskResultSchema, RemoveInterfaceTaskResultDetailSchema, SetTaskAuto, ExecuteTaskfrom config import Configrouter = APIRouter(prefix="/interface/task", tags=['自动化接口步骤'])@router.post('/insertTask', description="创建任务")async def insert_task(taskInfo: InsertInterfaceCaseTaskSchema,                      creator_user: User = Depends(Authentication())):    task = await InterfaceTaskMapper.save(        creator_user=creator_user,        **taskInfo.model_dump(            exclude_unset=True,            exclude_none=True        )    )    return Response.success(task)@router.post("/updateTask", description="修改")async def update_task(update: OptionInterfaceCaseTaskSchema,                      updater: User = Depends(Authentication())):    task = await InterfaceTaskMapper.update_by_id(        updateUser=updater,        **update.model_dump(exclude_unset=True,                            exclude_none=True)    )    return Response.success(task)@router.get("/detail", description="任务详情")async def get_task_detail(task: OptionInterfaceCaseTaskSchema = Depends(),                          _: User = Depends(Authentication())):    task = await InterfaceTaskMapper.get_by_id(ident=task.id)    return Response.success(task)@router.post("/removeTask", description='删除任务')async def removeTask(task: OptionInterfaceCaseTaskSchema, _: User = Depends(Authentication())):    await InterfaceTaskMapper.remove_task(taskId=task.id)    return Response.success()@router.post("/page", description="任务分页")async def pageTask(pageInfo: PageInterfaceCaseTaskSchema, _=Depends(Authentication())):    tasks = await InterfaceTaskMapper.page_by_module(**pageInfo.model_dump(        exclude_unset=True,        exclude_none=True    ))    return Response.success(tasks)@router.post("/association/cases", description="关联用例")async def association_cases(info: AssocCasesSchema, _: User = Depends(Authentication())):    exist_ = await InterfaceTaskMapper.association_cases(**info.model_dump())    return Response.success(exist_)@router.post("/association/apis", description="关联apis")async def association_cases(info: AssocApisSchema, _: User = Depends(Authentication())):    exist_ = await InterfaceTaskMapper.association_apis(**info.model_dump())    return Response.success(exist_)@router.post("/remove/association/cases", description="移除关联")async def association_case(info: RemoveAssocCasesSchema, _: User = Depends(Authentication())):    await InterfaceTaskMapper.remove_association_case(**info.model_dump())    return Response.success()@router.post("/remove/association/apis", description="移除api关联")async def association_case(info: RemoveAssocApisSchema, _: User = Depends(Authentication())):    await InterfaceTaskMapper.remove_association_api(**info.model_dump())    return Response.success()@router.get("/query/cases", description="查询关联用例")async def query_cases(info: GetByTaskId = Depends(), _: User = Depends(Authentication())):    cases = await InterfaceTaskMapper.query_case(info.taskId)    return Response.success(cases)@router.get("/query/apis", description="查询关联api用例")async def query_cases(info: GetByTaskId = Depends(), _: User = Depends(Authentication())):    apis = await InterfaceTaskMapper.query_apis(info.taskId)    return Response.success(apis)@router.post("/reorder/cases", description="重排用例")async def query_cases(info: AssocCasesSchema, _: User = Depends(Authentication())):    cases = await InterfaceTaskMapper.reorder_cases(**info.model_dump())    return Response.success(cases)@router.post("/reorder/apis", description="重排用例")async def query_cases(info: AssocApisSchema, _: User = Depends(Authentication())):    apis = await InterfaceTaskMapper.reorder_apis(**info.model_dump())    return Response.success(apis)@router.post('/execute', description="手动运行Task")async def executed_task(task: ExecuteTask, starter: User = Depends(Authentication())):    """    手动调用执行    :param task:    :param starter:    :return:    """    from common.redis_worker_pool import r_pool, register_interface_task_Handle    task_job = await InterfaceTaskMapper.get_by_id(task.task_id)    await r_pool.submit_to_redis(func=register_interface_task_Handle,                                 job_id=task_job.uid,                                 job_name=task_job.title,                                 job_kwargs={                                     "task_id": task_job.id,                                     "env_id": task.env_id,                                     "options": task.options,                                     "user": starter                                 })    return Response.success()@router.post('/executeByJenkins', description="Jenkins运行Task")async def executed_task(task: ExecuteTask):    """    外部调用执行    :param task:    :return:    """    from common.redis_worker_pool import r_pool, register_interface_task_RoBot    task_job = await InterfaceTaskMapper.get_by_id(task.task_id)    await r_pool.submit_to_redis(func=register_interface_task_RoBot,                                 job_id=task_job.uid,                                 job_name=task_job.title,                                 job_kwargs={                                     "task_id": task_job.id,                                     "env_id": task.env_id,                                     "options": ["API", "CASE"]                                 })    return Response.success()@router.get("/resultInfo", description="任务结果info")async def result_info(info: GetByTaskId = Depends(), _: User = Depends(Authentication())):    info = await InterfaceTaskResultMapper.get_by_id(info.taskId)    return Response.success(info)@router.post("/queryResults", description="查询结果")async def query_task_results(info: PageInterfaceTaskResultSchema, _: User = Depends(Authentication())):    results = await InterfaceTaskResultMapper.page_query(**info.model_dump(        exclude_unset=True,        exclude_none=True    ))    return Response.success(results)@router.post("/removeResult", description='移除任务结果')async def remove_task_result(result: RemoveInterfaceTaskResultDetailSchema, _: User = Depends(Authentication())):    await InterfaceTaskResultMapper.delete_by_id(result.resultId)    return Response.success()