#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/6/20# @Author : cyq# @File : part# @Software: PyCharm# @Desc:from fastapi import APIRouterfrom app.response import Responsefrom app.mapper.project.projectPart import ProjectPartMapperrouter = APIRouter(prefix="/part", tags=["模块"])@router.get("/query", description="查询所有模块")async def query_project():    """    查询所有模块    :return:    """    ps = await ProjectPartMapper.query_all()    return Response.success(ps)@router.get("/queryPartByProjectId")async def query_part_by_project_id(projectId: int):    """    根据项目id查询所有模块    返回树接口    :param projectId: 项目ID    :return:    """    ps = await ProjectPartMapper.query_by(projectID=projectId)    return Response.success(ps)@router.get("/queryRootPartByProjectId")async def query_root_part_by_project_id(projectId: int):    """    根据项目id查询root模块    返回树接口    :param projectId: 项目ID    :return:    """    ps = await ProjectPartMapper.query_parent_part_by_projectId(projectId)    return Response.success(ps)