#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/6/20# @Author : cyq# @File : project# @Software: PyCharm# @Desc:from fastapi import APIRouterfrom app.response import Responsefrom app.mapper.project import ProjectMapperrouter = APIRouter(prefix="/project", tags=["项目"])@router.get("/query", description="查询所有项目")async def query_project():    """    查询所有项目    :return:    """    ps = await ProjectMapper.query_all()    return Response.success(ps)