#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/2/10# @Author : cyq# @File : __init__.py# @Software: PyCharm# @Desc:from fastapi import APIRouterfrom fastapi.params import Dependsfrom app.controller import Authenticationfrom app.mapper.statistics import StatisticsMapperfrom app.model.base import Userfrom app.response import Responserouter = APIRouter(prefix="/statistics", tags=["统计"])@router.get("/all", description="全部统计")async def all_statistics():    data = await StatisticsMapper.all_statistics()    return Response.success(data)@router.get("/new/week", description="最近一周新增")async def new_week():    data = await StatisticsMapper.week_data()    return Response.success(data)@router.get("/task/week", description="最近一周运行情况")async def task_week():    data = await StatisticsMapper.task_data()    return Response.success(data)@router.get("/task/today", description="最近一周运行情况")async def task_current():    data = await StatisticsMapper.task_today_data()    return Response.success(data)