#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/11/7# @Author : cyq# @File : ui_report# @Software: PyCharm# @Desc:from fastapi import APIRouterfrom app.response import Responsefrom app.schema.ui.uiReport import PageTaskReportSchemafrom app.mapper.ui.uiReportMapper import UiTaskBaseReportMapperfrom utils import logrouter = APIRouter(prefix="/ui/report", tags=['自动化报告'])@router.post("/page/taskResult", description="自动化任务结果分页")async def get_task_result_page(pageInfo: PageTaskReportSchema):    log.debug(pageInfo)    data = await UiTaskBaseReportMapper.page_query(**pageInfo.dict(        exclude_none=True,    ))    return Response.success(data)