#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/6/25# @Author : cyq# @File : department# @Software: PyCharm# @Desc:from fastapi import APIRouterfrom app.response import Responsefrom app.mapper.user.departmentMapper import DepartmentMapperrouter = APIRouter(prefix="/department", tags=["部门"])@router.get("/query", description="部门表")async def query_department():    data = await DepartmentMapper.query_all()    return Response.success(data)