#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/6/10# @Author : cyq# @File : fileMapper# @Software: PyCharm# @Desc:from app.mapper import Mapperfrom model.file import FileModelfrom model import async_sessionfrom utils import logclass FileMapper(Mapper):    __model__ = FileModel    @staticmethod    async def insert_file(filePath: str, fileName: str) -> FileModel:        try:            async with async_session() as session:                file = FileModel(                    fileType="image/jpeg",                    filePath=filePath,                    fileName=fileName                )                session.add(file)                await session.commit()                return file        except Exception as e:            log.error(e)            raise e