#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/2/3# @Author : cyq# @File : playResultMapper# @Software: PyCharm# @Desc:from app.model import async_sessionfrom app.model.playUI.playStepContent import PlayStepContentResultfrom app.mapper import Mapperfrom utils import logclass PlayContentResultMapper(Mapper[PlayStepContentResult]):    __model__ = PlayStepContentResult    @classmethod    async def set_result(cls, result: PlayStepContentResult):        try:            async with async_session() as session:                async with session.begin():                    await cls.add_flush_expunge(session, result)        except Exception as e:            log.error(e)            raise e