#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/2/17# @Author : cyq# @File : dbConfigMapper# @Software: PyCharm# @Desc:from app.exception import CommonErrorfrom app.mapper import Mapperfrom app.model.base.db_config import DBConfigfrom enums import DBTypeEnumfrom utils import RedisClientfrom utils.mysqlClient import MySqlClientfrom utils.oracleClient import OracleClientclass DbConfigMapper(Mapper):    __model__ = DBConfig    @staticmethod    async def test_connect(db_type: int, db_host, db_port, db_username, db_password, db_database, **kwargs):        match db_type:            case DBTypeEnum.MYSQL:                return await MySqlClient.connect(db_host, db_port, db_username, db_password, db_database)            case DBTypeEnum.ORACLE:                return await OracleClient.connect(db_host, db_port, db_database, db_username, db_password)            case DBTypeEnum.REDIS:                return await RedisClient.connect(db_host, db_port, db_username, db_password, db_database)            case _:                raise CommonError("不支持类型")