#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/12/1# @Author : cyq# @File : jobMapper# @Software: PyCharm# @Desc:from sqlalchemy import selectfrom app.mapper import Mapperfrom app.model import async_sessionfrom app.model.base.job import AutoJobfrom app.mapper.interface.interfaceTaskMapper import InterfaceTaskMapperclass JobMapper(Mapper[AutoJob]):    __model__ = AutoJob    @classmethod    async def query_tasks_by_job_id(cls, job_id: str):        async with async_session() as session:            job = await cls.get_by_uid(uid=job_id, session=session)            if job.job_type == 1:                tasks =  await InterfaceTaskMapper.query_by_in_clause("uid", job.job_task_id_list,session)            else:                pass            return tasks