#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/6/6# @Author : cyq# @File : __init__.py# @Software: PyCharm# @Desc:from asyncio import current_taskfrom sqlalchemy.ext.asyncio import create_async_enginefrom sqlalchemy.ext.asyncio import AsyncSessionfrom sqlalchemy.ext.asyncio import async_sessionmakerfrom sqlalchemy.ext.asyncio import async_scoped_sessionfrom config import Configfrom .basic import BaseModelasync_engine = create_async_engine(Config.ASYNC_SQLALCHEMY_URI,                                   pool_size=50,  # 连接池大小                                   max_overflow=20,  # 允许溢出的连接数                                   pool_pre_ping=True,  # 连接前检查                                   pool_recycle=3600,  # 连接回收时间（秒）                                   pool_timeout=30,  # 获取连接超时时间                                   echo=False  # 生产环境设为False                                   )AsyncSessionMaker = async_sessionmaker(    async_engine,    class_=AsyncSession,    expire_on_commit=True,)async_session = async_scoped_session(    AsyncSessionMaker,    scopefunc=current_task)async def create_table():    try:        async with async_engine.begin() as conn:            await conn.run_sync(BaseModel.metadata.create_all,                                checkfirst=True)    except Exception as e:        raise e