#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/6/10# @Author : cyq# @File : file# @Software: PyCharm# @Desc:from sqlalchemy import Column, Stringfrom app.model.basic import BaseModelclass FileModel(BaseModel):    __tablename__ = "file"    fileName = Column(String(50), comment="附件名称")    fileType = Column(String(100), comment="附件格式")    filePath = Column(String(100), comment="路径")    def __init__(self, fileName: str, filePath: str, fileType: str = "jpg"):        self.fileName = fileName        self.fileType = fileType        self.filePath = filePath        super().__init__()    def __repr__(self):        return f"<{FileModel.__name__} {self.fileName}>"