#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/30# @Author : cyq# @File : playStepContent# @Software: PyCharm# @Desc:from app.model import BaseModelfrom sqlalchemy import Column, String, INTEGER, ForeignKey, TEXT, FLOAT, JSON, BOOLEANfrom enums.CaseEnum import PlayStepContentTypeclass PlayStepContent(BaseModel):    """    PlayStepContent    """    __tablename__ = "play_step_content"    content_name = Column(String(250), nullable=True, comment="步骤名称")    content_desc = Column(String(250), nullable=True, comment="步骤描述")    content_type = Column(INTEGER, default=PlayStepContentType.STEP_PLAY, nullable=False, comment="步骤类型")    enable = Column(BOOLEAN, default=1, nullable=False, comment="是否启用")    is_common = Column(BOOLEAN, default=0, nullable=False, comment="是否公共")    script_text = Column(TEXT, nullable=True, comment="脚本文本")    assert_list = Column(JSON, nullable=True, comment="断言")    target_id = Column(INTEGER, nullable=True, comment="外键 ID ")    def __repr__(self):        return f"<PlayStepContent(id={self.id}, content_name={self.content_name}, content_desc={self.content_desc}, content_type={self.content_type}, target_id={self.target_id},is_common_api={self.is_common}, enable={self.enable})>"