#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/12/11# @Author : cyq# @File : aps_jobs# @Software: PyCharm# @Desc: APS 定时任务from app.model.base.job import AutoJobfrom utils import logfrom common.redis_worker_pool import r_pool, register_interface_task_RoBot, register_play_task_robotasync def aps_submit_interface_task(job: AutoJob):    """    调度任务执行    :param job: AutoJob 任务    """    if not r_pool.is_running:        raise RuntimeError("任务池未启动")    ## 添加到多个任务    ##    for task_id in job.job_task_id_list:        await r_pool.submit_to_redis(            func=register_interface_task_RoBot,            job_id=task_id,            job_name=job.job_name,            job_kwargs={                "task_id": task_id,                "env_id": job.job_env_id,                "retry": job.job_max_retry_count,                "retry_interval": job.job_retry_interval,                "notify_id": job.notify_id,                "variables": job.job_kwargs            }        )async def aps_submit_play_task(job: AutoJob):    """    调度任务执行    :param job: AutoJob 任务    """    if not r_pool.is_running:        raise RuntimeError("任务池未启动")    ## 添加到多个任务    ##    for task_id in job.job_task_id_list:        await r_pool.submit_to_redis(            func=register_play_task_robot,            job_id=task_id,            job_name=job.job_name,            job_kwargs={                "task_id": task_id,                "retry": job.job_max_retry_count,                "retry_interval": job.job_retry_interval,                "notify_id": job.notify_id,                "variables": job.job_kwargs            }        )async def aps_heartbeat():    """    可序列化的心跳任务函数    """    from common import rc    Key = "scheduler:master_lock"    await rc.init_pool()    if await rc.r.get(Key):        await rc.r.expire(Key, 60)        log.info("[Scheduler] : ✅ Renewed lock")async def print_jobs():    """    打印JOB    """    from .scheduler import hubScheduler    jobs = await hubScheduler.get_jobs()    for job in jobs:        log.info(f"[Scheduler]: JOB {job.id} {job} ")