#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/4/7# @Author : cyq# @File : tasks# @Software: PyCharm# @Desc:from enums import StarterEnumfrom interface.taskRunner import TaskRunnerfrom interface.starter import APIStarterfrom play.player import Playerfrom play.starter import UIStarterfrom app.scheduler.celer7.base import celery_app@celery_app.task(name="execute_api_task", bind=True)async def execute_api_task(task_id: int):    """    执行API任务    :param task_id:    :return:    """    await TaskRunner(APIStarter(StarterEnum.RoBot)).runTask(task_id)@celery_app.task(name="execute_ui_task", bind=True)async def execute_ui_task(task_id: int):    """    执行UI任务    :param task_id:    :return:    """    await Player(UIStarter(StarterEnum.RoBot)).run_task(task_id)