#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/4/8# @Author : cyq# @File : apsSchema# @Software: PyCharm# @Desc:from typing import Literal, Listfrom pydantic import BaseModel, Fieldfrom enums import TriggerTypeclass InsertJobSchema(BaseModel):    job_type: int = Field(..., description="任务类型")    job_name: str = Field(..., description="任务名称")    job_task_ids: List[str] = Field(..., description="任务id")    job_enabled: bool = Field(True, description="是否启用")    job_trigger_type: TriggerType = Field(..., description="触发器类型")    job_execute_time: str | None = Field(None, description="执行时间")    job_execute_cron: str | None = Field(None, description="执行cron")    job_execute_interval: int | None = Field(None, description="执行间隔")    job_max_retry_count: int | None = Field(None, description="最大重试次数")    job_retry_interval: int | None = Field(None, description="重试间隔")    job_executor: str | None = Field("AsyncIOScheduler", description="执行器")    job_kwargs: dict | None = Field(None, description="执行参数")    job_push_enabled: bool = Field(False, description="是否推送")    job_push_id: int | None = Field(None, description="推送id")    project_id: int = Field(..., description="项目id")class UpdateJobSchema(BaseModel):    job_id: int = Field(..., description="任务id")    job_name: str | None = Field(..., description="任务名称")    job_task_ids: List[str] | None = Field(..., description="任务id")    job_enabled: bool | None = Field(True, description="是否启用")    job_trigger_type: TriggerType | None = Field(..., description="触发器类型")    job_execute_time: str | None = Field(None, description="执行时间")    job_execute_corn: str | None = Field(None, description="执行corn")    job_execute_interval: int | None = Field(None, description="执行间隔")    job_max_retry_count: int | None = Field(None, description="最大重试次数")    job_retry_interval: int | None = Field(None, description="重试间隔")    job_executor: str | None = Field("AsyncIOScheduler", description="执行器")    job_kwargs: dict | None = Field(None, description="执行参数")    job_push_enabled: bool | None = Field(False, description="是否推送")    job_push_id: int | None = Field(None, description="推送id")    project_id: int = Field(..., description="项目id")class GetOrRemoveJobSchema(BaseModel):    job_id: int = Field(..., description="任务id")class ApsSwitchSchema(BaseModel):    tag: Literal["API", "UI"] = Field()    uid: str = Field()    switch: bool