#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/9/29# @Author : cyq# @File : departSchema# @Software: PyCharm# @Desc:from pydantic import Field, BaseModelfrom app.schema import PageSchemaclass InsertDepartmentSchema(BaseModel):    """    添加部门    """    name: str = Field(..., description="部门名称")    description: str = Field(..., description="部门描述")    admin_id: int = Field(..., description="部门负责人ID")    admin_name: str = Field(..., description="部门负责人名称")class UpdateDepartmentSchema(BaseModel):    """    更新部门    """    id: int = Field(..., description="部门ID")    name: str | None = Field(None, description="部门名称")    description: str | None = Field(None, description="部门描述")    admin_id: int | None = Field(None, description="部门负责人ID")    admin_name: str | None = Field(None, description="部门负责人名称")class GetDepartmentSchema(BaseModel):    """    获取部门    """    id: int = Field(..., description="部门ID")class PageDepartmentSchema(PageSchema):    """    获取部门    """    name: str | None = Field(None, description="部门名称")    admin_id: int | None = Field(None, description="部门负责人ID")class AddDepartTagSchema(BaseModel):    """    添加部门标签    """    depart_id: int = Field(..., description="部门ID")    tag_name: str = Field(..., description="标签名称")class UpdateDepartTagSchema(BaseModel):    """    添加部门标签    """    depart_id: int = Field(..., description="部门ID")    tag_name: str | None = Field(None, description="标签名称")class RemoveDepartTagSchema(BaseModel):    """    添加部门标签    """    tag_id: int = Field(..., description="标签ID")