#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/7/2# @Author : cyq# @File : _baseSchema# @Software: PyCharm# @Desc:from typing import Dictfrom pydantic import BaseModel, Fieldfrom app.exception import ParamsErrorclass BaseSchema:    @staticmethod    def not_empty(value):        if isinstance(value, str) and len(value.strip()) == 0:            raise ParamsError("不能为空")        if not isinstance(value, int):            if not value:                raise ParamsError("不能为空")        return valueclass PageSchema(BaseModel):    """    分页参数    """    current: int = 1    pageSize: int = 10    sort: dict | str | None = Field(default=None, description="排序参数，接受 JSON 字典格式")