#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/10/15# @Author : cyq# @File : uiStep# @Software: PyCharm# @Desc:from typing import Listfrom pydantic import BaseModelfrom app.schema import PageSchemaclass UIStepField(BaseModel):    id: int | None    uid: str | None    name: str | None    description: str | None    method: str | None    locator: str | None    value: str | None    iframe_name: str | None    new_page: bool = None    is_ignore: bool = None    creator: int | None    creatorName: str | None    is_common_step: bool | Noneclass AddUICaseStep(UIStepField):    case_id: int | None    name: str    description: str    new_page: bool = False    is_ignore: bool = False    creator: int | None    creatorName: str | None    is_common_step: bool = Falseclass PutUICaseStep(UIStepField):    id: intclass PageUICaseStep(UIStepField, PageSchema):    ...class RemoveUIStepSchema(BaseModel):    stepId: int    caseId: int = Noneclass ReOrderUIStepSchema(BaseModel):    stepIds: List[int]    caseId: intclass CopyUIStepSchema(BaseModel):    stepId: int    caseId: int