#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/10/9# @Author : cyq# @File : uiTask# @Software: PyCharm# @Desc:from pydantic import BaseModel, validatorfrom app.schema import BaseSchema, PageSchemafrom app.schema.ui.uiCase import UICaseFieldclass UITaskField(BaseModel):    projectId: int | None    casePartId: int | None    uid: str | None    title: str | None    level: str | None    status: str | None    creatorName: str | Noneclass PageTaskSchema(UITaskField, PageSchema):    ...class PageTaskCaseSchema(UICaseField, PageSchema):    taskId: strclass AddTaskCaseSchema(BaseModel):    taskUid: str    caseIdList: list[int]    @validator("taskUid")    def not_empty(cls, v):        return BaseSchema.not_empty(v)class RemoveTaskCaseSchema(BaseModel):    taskId: int    caseId: int    @validator("taskId", "caseId")    def not_empty(cls, v):        return BaseSchema.not_empty(v)class RemoveTaskSchema(BaseModel):    taskId: str    @validator("taskId")    def not_empty(cls, v):        return BaseSchema.not_empty(v)class UpdateTaskSchema(BaseModel):    uid: str    casePartId: int | None = None    projectId: int | None = None    desc: str | None = None    isAuto: bool | None = None    isSend: bool | None = None    cron: str | None = None    level: str | None = None    status: str | None = None    retry: int | None = None    switch: bool | None = None    title: str | None = None    sendType: str | None = None    sendKey: str | None = None    mode: str | None = Noneclass NewTaskSchema(BaseModel):    casePartId: int    projectId: int    creator: int    desc: str    isAuto: bool = False    isSend: bool = False    cron: str | None = None    mode: str    level: str = "P1"    status: str = "WAIT"    retry: int = 0    title: str    ui_case_num: int = 0    @validator("casePartId", 'projectId', 'creator', "title", "desc")    def not_empty(cls, v):        return BaseSchema.not_empty(v)class SetTaskSwitch(BaseModel):    uid: str    switch: bool