#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/3/26# @Author : cyq# @File : autoGenClient# @Software: PyCharm# @Desc:import asynciofrom autogen_agentchat.agents import AssistantAgent, UserProxyAgentfrom autogen_ext.models.openai import OpenAIChatCompletionClientDEEPSEEK_API_KEY = "sk-"DEEPSEEK_API_BASE = "https://api.deepseek.com/v1"async def main() -> None:    deepseek_client = OpenAIChatCompletionClient(model="deepseek-chat",                                                 base_url=DEEPSEEK_API_BASE,                                                 api_key=DEEPSEEK_API_KEY,                                                 model_info={"vision": False,                                                             "function_calling": False,                                                             "json_output": False,                                                             "family": "DeepSeek"}, )    # 配置 AutoGen 代理    assistant = AssistantAgent(        name="DeepSeek_Assistant",        model_client=deepseek_client,        system_message="你是一个由 DeepSeek 模型驱动的助手。请回答问题。",    )    res = await assistant.run(task="hi")    print(res)    await deepseek_client.close()if __name__ == '__main__':    asyncio.run(main())