#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/6/26# @Author : cyq# @File : dubboClient# @Software: PyCharm# @Desc:from dubbo.client import DubboClient, ZkRegisterdef call_dubbo_service():    # zk = ZkRegister(hosts='127.0.0.1:2181')    client = DubboClient(        interface='org.hub.ch_v3.dubbo.DemoService',        version='1.0.0',  # 添加：版本号        host="127.0.0.1:20880",    )    result = client.call(        method='hello',        args=("cyq",)    )    print(result)    return resultif __name__ == '__main__':    call_dubbo_service()