#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/21# @Author : cyq# @File : __init__.py# @Software: PyCharm# @Desc:from enums.CaseEnum import CaseStepContentTypefrom .step_content_api import APIStepContentStrategyfrom .step_content_assert import APIAssertsContentStrategyfrom .step_content_condition import APIConditionContentStrategyfrom .step_content_db import APIDBContentStrategyfrom .step_content_group import APIGroupContentStrategyfrom .step_content_loop import APILoopContentStrategyfrom .step_content_script import APIScriptContentStrategyfrom .step_content_wait import APIWaitContentStrategydef get_step_strategy(step_type: int, interface_executor):    """获取步骤执行策略"""    match step_type:        case CaseStepContentType.STEP_API:            return APIStepContentStrategy(interface_executor)        case CaseStepContentType.STEP_API_GROUP:            return APIGroupContentStrategy(interface_executor)        case CaseStepContentType.STEP_API_WAIT:            return APIWaitContentStrategy(interface_executor)        case CaseStepContentType.STEP_API_SCRIPT:            return APIScriptContentStrategy(interface_executor)        case CaseStepContentType.STEP_API_CONDITION:            return APIConditionContentStrategy(interface_executor)        case CaseStepContentType.STEP_LOOP:            return APILoopContentStrategy(interface_executor)        case CaseStepContentType.STEP_API_DB:            return APIDBContentStrategy(interface_executor)        case CaseStepContentType.STEP_API_ASSERT:            return APIAssertsContentStrategy(interface_executor)        case _:            raise Exception(f"Unknown step type: {step_type}")