#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/30# @Author : cyq# @File : __init__.py# @Software: PyCharm# @Desc:from dataclasses import dataclassfrom typing import Optionalfrom playwright.async_api import Pagefrom app.model.playUI import PlayStepV2, PlayCaseResultfrom croe.interface.manager.variable_manager import VariableManagerfrom croe.play.starter import UIStarter@dataclassclass StepContext:    page: Page    step: PlayStepV2    starter: UIStarter    variable_manager: VariableManager    case_result: Optional[PlayCaseResult] = None    @property    def selector(self) -> Optional[str]:        return self.step.selector    @property    def getter(self) -> Optional[str]:        return self.step.getter    @property    def method(self) -> Optional[str]:        return self.step.method    @property    def value(self) -> Optional[str]:        return self.step.value__all__ = [    "StepContext",]