#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/30# @Author : cyq# @File : __init__.py# @Software: PyCharm# @Desc:from dataclasses import dataclassfrom typing import Optionalfrom playwright.async_api import Pagefrom app.model.playUI import PlayStepModel, PlayCaseResult, PlayCase, PlayTaskResultfrom app.model.playUI.playStepContent import PlayStepContentfrom croe.interface.manager.variable_manager import VariableManagerfrom croe.play.starter import UIStarter@dataclassclass StepContext:    page: Page    step: PlayStepModel    starter: UIStarter    variable_manager: VariableManager    case_result: Optional[PlayCaseResult] = None    @property    def selector(self) -> Optional[str]:        return self.step.selector    @property    def getter(self) -> Optional[str]:        return self.step.getter if self.step.getter else None    @property    def method(self) -> Optional[str]:        return self.step.method    @property    def value(self) -> Optional[str]:        return self.step.value    async def log(self, msg: str):        await self.starter.send(msg)@dataclassclass StepContentContext:    index: int    play_step_content: PlayStepContent    execution_context: StepContext    starter: UIStarter    variable_manager: VariableManager@dataclassclass ExecutionContext:    """    执行上下文    """    play_case: PlayCase    starter: UIStarter    case_result: Optional[PlayCaseResult] = None    task_result: Optional[PlayTaskResult] = None__all__ = [    "StepContext",    "StepContentContext",    "ExecutionContext"]