#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/30# @Author : cyq# @File : __init__.py# @Software: PyCharm# @Desc:from croe.play.executor.step_content.play_step_content import PlayStepContentStrategyfrom enums.CaseEnum import PlayStepContentTypedef get_play_step_strategy(step_type: int, executor):    match step_type:        case PlayStepContentType.STEP_PLAY:            return PlayStepContentStrategy(executor)        case _:            raise Exception(f"Unknown step type: {step_type}")