#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/30# @Author : cyq# @File : play_step_content# @Software: PyCharm# @Desc:from app.mapper.play import PlayStepV2Mapperfrom ._base import StepBaseStrategyfrom croe.play.context import StepContentContext, StepContextclass PlayStepContentStrategy(StepBaseStrategy):    """    ui 单步骤执行    """    async def execute(self, step_content: StepContentContext) -> bool:        step = await PlayStepV2Mapper.get_by_id(step_content.play_step_content.target_id)        step_context = StepContext(            step=step,            page=step_content.page,            starter=step_content.starter,            variable_manager=step_content.variable_manager        )        SUCCESS, page = await self.play_executor.execute(            step_context        )        if page:            step_content.page = page        return SUCCESS