from abc import ABC, abstractmethodfrom playwright.async_api import Locatorfrom croe.play.context import StepContextfrom utils import logclass LocatorMethods(ABC):    getter_name: str = None  # play step getter    @abstractmethod    async def locator(self, context) -> Locator:        """        执行具体的处理逻辑        子类必须实现此方法，定义具体的操作执行逻辑。        Args:            context: 步骤上下文，包含页面对象、步骤信息和变量转换器        """        passclass GetByAltText(LocatorMethods):    """    通过 alt 文本定位元素（通常用于图片）    示例：        <img alt="User Avatar">    """    getter_name = "get_by_alt_text"    async def locator(self, context: StepContext) -> Locator:        """        通过 alt 属性文本获取定位器        Args:            context: 步骤上下文，包含页面对象和选择器        Returns:            Locator: 匹配的定位器对象        """        locator = context.page.get_by_alt_text(context.selector)        log.info(f"get_by_alt_text: {context.selector}")        return locatorclass GetByLabel(LocatorMethods):    """    通过关联的 label 文本定位表单元素    示例：        <label for="username">Username:</label>        <input id="username">    """    getter_name = "get_by_label"    async def locator(self, context: StepContext) -> Locator:        """        通过 label 文本获取定位器        Args:            context: 步骤上下文，包含页面对象和选择器        Returns:            Locator: 匹配的定位器对象        """        locator = context.page.get_by_label(text=context.selector, exact=True)        log.info(f"get_by_label: {context.selector}")        return locatorclass GetByPlaceholder(LocatorMethods):    """    通过 placeholder 属性定位输入框    示例：        <input placeholder="Enter your name">    """    getter_name = "get_by_placeholder"    async def locator(self, context: StepContext) -> Locator:        """        通过 placeholder 属性获取定位器        Args:            context: 步骤上下文，包含页面对象和选择器        Returns:            Locator: 匹配的定位器对象        """        locator = context.page.get_by_placeholder(text=context.selector, exact=True)        log.info(f"get_by_placeholder: {context.selector}")        return locatorclass GetByTestId(LocatorMethods):    """    通过 data-testid 属性定位元素    示例：        <button data-testid="submit-button">Submit</button>    """    getter_name = "get_by_test_id"    async def locator(self, context: StepContext) -> Locator:        """        通过 data-testid 属性获取定位器        Args:            context: 步骤上下文，包含页面对象和选择器        Returns:            Locator: 匹配的定位器对象        """        locator = context.page.get_by_test_id(context.selector)        log.info(f"get_by_test_id: {context.selector}")        return locatorclass GetByText(LocatorMethods):    """    通过文本内容定位元素    示例：        <span>Welcome</span>    """    getter_name = "get_by_text"    async def locator(self, context: StepContext) -> Locator:        """        通过文本内容获取定位器        Args:            context: 步骤上下文，包含页面对象和选择器        Returns:            Locator: 匹配的定位器对象        """        locator = context.page.get_by_text(text=context.selector, exact=True)        log.info(f"get_by_text: {context.selector}")        return locatorclass GetByTitle(LocatorMethods):    """    通过 title 属性定位元素    示例：        <button title="Save changes">Save</button>    """    getter_name = "get_by_title"    async def locator(self, context: StepContext) -> Locator:        """        通过 title 属性获取定位器        Args:            context: 步骤上下文，包含页面对象和选择器        Returns:            Locator: 匹配的定位器对象        """        locator = context.page.get_by_title(text=context.selector, exact=True)        log.info(f"get_by_title: {context.selector}")        return locator