#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/29# @Author : cyq# @File : content# @Software: PyCharm# @Desc:from dataclasses import dataclassfrom typing import Optional, Dict, Anyfrom playwright.async_api import Pagefrom app.model.playUI import PlayStep, PlayCaseResultfrom croe.ui.starter import UIStarterfrom croe.variable_manager import VariableManager@dataclassclass StepContext:    page: Page    step: PlayStep    starter: UIStarter    variable_manager: VariableManager    case_result: Optional[PlayCaseResult] = None    @property    def selector(self):        return self.step.locator if self.step.locator else ""