#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/30# @Author : cyq# @File : page_methods# @Software: PyCharm# @Desc: https://playwright.dev/python/docs/api/class-pagefrom croe.play.context import StepContextfrom utils import logfrom ._base_method import BaseMethodsclass ClickByAltTextMethod(BaseMethods):    """    <img alt='Playwright logo'>    """    name = "click_by_alt_text"    async def execute(self, context: StepContext) -> None:        element = context.page.get_by_alt_text(context.selector)        await element.fill(context.value)        log.debug(f"[ClickByAltTextMethod] get_by_alt_text ：{element}")        await element.click()        log.debug(f"[ClickByAltTextMethod] click_by_alt_text")class ClickByLabelMethod(BaseMethods):    """    <input aria-label="Username">    <label for="password-input">Password:</label>    <input id="password-input">    """    name = "click_by_label"    async def execute(self, context: StepContext) -> None:        element = context.page.get_by_label(context.selector)        log.debug(f"[ClickByLabelMethod] get_by_label ：{element}")        await element.click()        log.debug(f"[ClickByLabelMethod] click_by_label")