#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/29# @Author : cyq# @File : __init__.py# @Software: PyCharm# @Desc:from typing import Optional, Anyclass UIAutomationError(Exception):    """    UI自动化基础异常类    所有UI自动化相关异常的基类，提供统一的错误消息和详细信息格式。    """    base_error = "UI自动化错误"    def __init__(self, message: str, details: Optional[Any] = None):        """        初始化UI自动化异常        Args:            message: 错误消息            details: 错误详细信息，可以是任意类型        """        self.message = message        self.details = details        super().__init__(self.message)class BrowserError(UIAutomationError):    """    浏览器相关异常    当浏览器启动、连接或操作失败时抛出。    """    passclass ElementNotFoundError(UIAutomationError):    """    元素未找到异常    当无法定位到页面元素时抛出。    """    passclass TimeoutError(UIAutomationError):    """    超时异常    当等待操作超时时抛出。    """    passclass ActionError(UIAutomationError):    """    动作执行异常    当执行UI动作（如点击、输入等）失败时抛出。    """    passclass AssertionFailedError(UIAutomationError):    """    断言失败异常    当断言检查失败时抛出。    """    passclass VariableError(UIAutomationError):    """    变量相关异常    当变量操作（如获取、设置、转换）失败时抛出。    """    passclass ConditionError(UIAutomationError):    """    条件检查异常    当条件判断失败时抛出。    """    passclass APIAssertException(AssertionError):    """    API断言异常    当API断言检查失败时抛出。    """    passclass RetryException(Exception):    """    重试异常    用于触发重试机制的异常。    """    pass__all__ = [    "UIAutomationError",    "BrowserError",    "ElementNotFoundError",    "TimeoutError",    "ActionError",    "AssertionFailedError",    "VariableError",    "ConditionError",    "APIAssertException",    "RetryException",]