#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/29# @Author : cyq# @File : method_chain# @Software: PyCharm# @Desc:from typing import List, Optionalfrom croe.ui.methods import *from croe.ui.methods.base import BaseMethodsclass MethodChain:    def __init__(self):        self._methods: List[BaseMethods] = []        self._chain: Optional[BaseMethods] = None    def add_method(self, method: BaseMethods) -> "MethodChain":        """        添加处理器到链中        将处理器添加到处理器列表，支持链式调用。        Args:            method: 要添加的处理器实例        Returns:            HandlerChain: 返回自身，支持链式调用        """        self._methods.append(method)        return self    def build(self) -> BaseMethods:        """        构建处理器链        将所有添加的处理器按顺序连接成责任链，并返回链头。        Returns:            BaseHandler: 责任链的头部处理器        Raises:            ValueError: 当没有添加任何处理器时抛出        """        if not self._methods:            raise ValueError("No handlers added to chain")        if self._chain is None:            self._chain = self._methods[0]        for i in range(len(self._methods) - 1):            self._methods[i].set_next(self._methods[i + 1])        return self._chain    @classmethod    def create_default_chain(cls) -> BaseMethods:        """        创建默认的处理器链        创建包含所有标准处理器的默认责任链，按功能分类排列。        Returns:            BaseHandler: 构建完成的默认处理器链        """        chain = cls()        chain.add_method(CheckMethod())        chain.add_method(ClickMethod())        chain.add_method(FillMethod())        chain.add_method(FocusMethod())        chain.add_method(SelectMethod())        chain.add_method(TypeMethod())        chain.add_method(AssertTextMethod())        chain.add_method(AssertValueMethod())        chain.add_method(AssertVisibleMethod())        chain.add_method(AssertEnabledMethod())        chain.add_method(AssertDisabledMethod())        chain.add_method(AssertCheckedMethod())        chain.add_method(AssertUncheckedMethod())        chain.add_method(GetAttrMethod())        chain.add_method(GetTextMethod())        chain.add_method(GetValueMethod())        chain.add_method(GetCountMethod())        chain.add_method(GotoMethod())        chain.add_method(ReloadMethod())        return chain.build()