#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2026/1/29# @Author : cyq# @File : script_methods# @Software: PyCharm# @Desc:from .base import BaseMethodsfrom ..exceptions import ActionErrorfrom .context import StepContextclass ScriptMethod(BaseMethods):    handler_name = "play_evaluate"    async def execute(self, context: StepContext) -> None:        script = await context.var_trans.trans(context.step.fill_value)        try:            result = await context.page.evaluate(script)            await context.starter.send(f"执行脚本 ✅ : {script}")            if result is not None:                await context.starter.send(f"脚本结果: {result}")        except Exception as e:            raise ActionError(f"Script execution failed", details=str(e))