#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/12/6# @Author : cyq# @File : io_sender# @Software: PyCharm# @Desc:from utils import GenerateTools, MyLogurufrom app.ws import iolog = MyLoguru().get_logger()class APISocketSender:    def __init__(self, uid: str = None):        self.logs = []        self.uid = uid    async def send(self, msg: str):        try:            msg = f"{GenerateTools.getTime(1)} ðŸš€ ðŸš€  {msg}"            log.info(msg)            self.logs.append(msg + "\n")            await io.log_emit(msg, uid=self.uid)        except Exception as e:            log.error(e)    async def over(self, reportId: int = None):        return await io.log_emit_over(rId=reportId, uid=self.uid)