#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/11/19# @Author : cyq# @File : runner# @Software: PyCharm# @Desc:from typing import List, Dict, Any, Mappingimport httpxfrom httpx import Response, ConnectErrorfrom app.mapper.project.env import EnvMapperfrom app.model.base import Userfrom app.model.interface import InterfaceModelfrom enums import InterfaceExtractTargetVariablesEnumfrom utils import MyLoguru, GenerateToolsfrom app.mapper.interface import InterfaceMapper, InterfaceCaseMapperfrom .execAssert import ExecAssertsfrom .execExtract import ExecResponseExtractfrom .execScript import ExecScriptForInterfacefrom .sender import Senderfrom .writer import InterfaceAPIWriterclass InterFaceRunner:    response: Response = None    def __init__(self, starter: User):        self.starter = starter        self.variables = {}        self.log = MyLoguru().get_logger()        self.sender = Sender(self.variables, self.log)    async def set_variables(self, data: Dict[str, Any] | List[Dict[str, Any]]) -> Dict[str, Any]:        """        设置变量        :param data:        :return:        """        if isinstance(data, dict):            self.variables.update(**data)        elif isinstance(data, list):            data = await GenerateTools.list2dict(data)            self.variables.update(**data)        return data    async def try_interface(self, interfaceId: int):        """        执行单个接口请求调试        无变量、有前置方法、        需要返回response        """        interface = await InterfaceMapper.get_by_id(ident=interfaceId)        return await self.__execute_interface(interface, no_db=True)    async def run_interCase(self, caseId: int):        """        执行接口用例        """        interfaces = await InterfaceCaseMapper.query_interface_by_caseId(caseId=caseId)        for index, interface in enumerate(interfaces, start=1):            self.log.info(f"step {index} : {interfaces}")            response = await self.__execute_interface(interface)            return    async def __execute_interface(self,                                  interface: InterfaceModel,                                  no_db: bool = False):        """        API 执行        """        url = await self.set_req_url(interface)        try:            temp_variables = []            # 0、接口处理请求URL            # 1、前置变量参数            bp = await self.__exec_before_params(interface.beforeParams)            temp_variables.extend(bp)            # 2、执行前置函数            bs = await self.__exec_before_script(interface.beforeScript)            temp_variables.extend(bs)            # 3、执行接口请求            self.response = await self.sender(                url=url,                interface=interface)            # 4、进行断言            asserts_info = await self.__exec_assert(interface)            # 5、出参提取            ei = await self.__exec_extract(interface)            temp_variables.extend(ei)            # 6、执行后置函数            await self.__exec_after_script(interface.afterScript)            self.log.debug(f"temp variables: {temp_variables}")            return await InterfaceAPIWriter.writer_interface_result(                no_db=no_db,                starter=self.starter,                interface=interface,                response=self.response,                asserts=asserts_info,                variables=temp_variables)        except ConnectError as e:            self.log.error(e)            return f"{str(e)} to {url}"        except Exception as e:            self.log.exception(e)            return f"{str(e)} to {url}"    async def __exec_before_script(self, script: str) -> List[Any] | List[Mapping[str, Any]]:        """处理前置脚本"""        if script:            exe = ExecScriptForInterface(funcStr=script, log=self.log)            extracted_vars = await self.set_variables(exe.exec_beforeFunc())            self.log.debug(f"前置脚本 = {extracted_vars}")            _vars = [                {                    InterfaceExtractTargetVariablesEnum.KEY: k,                    InterfaceExtractTargetVariablesEnum.VALUE: v,                    InterfaceExtractTargetVariablesEnum.Target: InterfaceExtractTargetVariablesEnum.BeforeScript                }                for k, v in extracted_vars.items()            ]            return _vars        return []    async def __exec_before_params(self, before_params: List[Dict[str, Any]] = None):        """处理前置参数"""        if before_params:            extracted_vars = await self.set_variables(before_params)            self.log.debug(f"前置参数 = {extracted_vars}")            _vars = [                {                    InterfaceExtractTargetVariablesEnum.KEY: k,                    InterfaceExtractTargetVariablesEnum.VALUE: v,                    InterfaceExtractTargetVariablesEnum.Target: InterfaceExtractTargetVariablesEnum.BeforeParams                }                for k, v in extracted_vars.items()            ]            return _vars        return []    async def __exec_after_script(self, script: str):        ...    async def __exec_assert(self, interface: InterfaceModel):        """响应断言"""        self.log.debug(interface.asserts)        if interface.asserts and self.response:            _assert = ExecAsserts(self.response)            return await _assert(interface.asserts)    async def __exec_extract(self, interface: InterfaceModel):        """变量提取"""        if interface.extracts and self.response:            _extract = ExecResponseExtract(response=self.response)            _vars = await _extract(interface.extracts)            self.log.debug(f"响应参数提取 = {_vars}")            await self.set_variables(_vars)            return _vars        return []    @staticmethod    async def set_req_url(interface: InterfaceModel):        env = await EnvMapper.get_by_id(ident=interface.env_id)        domain = env.host        if env.port:            domain += f":{env.port}"        return domain + interface.url