#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/12/18# @Author : cyq# @File : starter# @Software: PyCharm# @Desc:import enumfrom app.model.base import Userfrom enums import StarterEnumclass Starter:    starterName: str    startBy: int    userId: int = None    def __init__(self, startBy: StarterEnum | User):        if isinstance(startBy, User):            self.startBy = 1            self.starterName = startBy.username            self.userId = startBy.id        else:            self.startBy = startBy.value    @property    def username(self):        if self.startBy == StarterEnum.User.value:            return self.starterName        return StarterEnum(self.startBy).nameif __name__ == '__main__':    s = Starter(2)    print(s.username)