import asynciofrom app import appfrom apscheduler.schedulers.asyncio import AsyncIOSchedulerfrom app.scheduler import Scheduler@app.on_event("startup")async def init_aps():    """    启动aps 任务    :return:    """    from config import Config    if Config.APS:        aps = AsyncIOScheduler()        await Scheduler.init(aps)        # todo UI截图删除        Scheduler.start()@app.on_event("startup")async def init_db():    """    初始化数据库    :return:    """    from model.basic import create_table    asyncio.create_task(create_table())@app.on_event("shutdown")def shutdown_aps():    Scheduler.shutdown()