#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/1/3# @Author : cyq# @File : miniFlask# @Software: PyCharm# @Desc:import jsonimport timefrom flask import Flask, request, jsonifyfrom flask_cors import CORSfrom flask import Responseapp = Flask("miniFlask")app.config['JSON_AS_ASCII'] = FalseCORS(app, supports_credentials=True)@app.route("/mini/login", methods=["POST"])def login():    try:        parse = request.get_json()        username = parse.get("username")        password = parse.get("password")        time.sleep(2)        return jsonify({"code": 0, "data": {"info": {"name": username, "password": password, "age": 20}, "number": 200,                                            "flag": True,                                            "list": [1, "name", True, {"hello": "world"}]}, "msg": "success"})    except Exception:        return jsonify({"code": 100, "data": None, "msg": "error"})@app.route("/mini/queryUser", methods=["GET"])def queryUser():    parse = request.get_json()    cookie = parse.get("cookie")    time.sleep(2)    if cookie == "im cookie":        users = []        for i in range(10):            users.append({"username": "asd", "age": i})        return jsonify({"code": 0, "data": users, "msg": "success"})    else:        return jsonify({"code": 100, "data": None, "msg": "error"})@app.route("/mini/timeout", methods=["GET"])def timeout():    time.sleep(100)    return jsonify({"code": 0, "data": "123", "msg": "success"})@app.route("/mini/serverError", methods=["GET"])def serverError():    return jsonify({"code": 0, "data": 1 / 0, "msg": "success"})@app.route("/mini/forbidden", methods=["GET"])def forbidden():    return Response(json.dumps({"code": 100, "data": None, "msg": "auth error"}), status=403)@app.route("/mini/text", methods=["get"])def text():    time.sleep(4)    return "kdjakjdhakjhdakjdhajkdhiu,kfghdkghdkladkas"if __name__ == '__main__':    app.run(host="127.0.0.1", port=6006)