#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/12/3# @Author : cyq# @File : android# @Software: PyCharm# @Desc:import uiautomator2 as ufrom uiautomator2 import ConnectError, Devicefrom utils import MyLogurulog = MyLoguru().get_logger()class U2:    @classmethod    def u2(cls, ip: str) -> [Device, None]:        try:            driver = u.connect(ip)            log.info(f"[connect]->{ip}")            driver.implicitly_wait(20.0)            return driver        except ConnectError as e:            log.error(repr(e))            raise e    @staticmethod    def start_app(driver: "u"):        driver.app_start(package_name="com.wiwj.uat5iwork", )if __name__ == '__main__':    u = U2.u2("10.1.4.121:5555")    U2.start_app(u)