#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/7/17# @Author : cyq# @File : logWriter# @Software: PyCharm# @Desc:from utils import GenerateTools, logimport asyncioclass LogWriter:    def __init__(self, caseId: str = None, taskId: str = None):        self.logs = []        self.caseId = caseId        self.taskId = taskId    async def write_log(self, msg: str):        """        å†™å…¥dbæ—¥å¿—        :param msg:        :return:        """        try:            formatMsg = f"{GenerateTools.getTime(1)} ðŸŸ° {msg} \n"            log.info(f"TASK:{self.taskId} CASE :{self.caseId} : {formatMsg.strip()}")            self.logs.append(formatMsg)        except Exception as e:            raise e    async def clear(self):        self.logs = []