#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/2/27# @Author : cyq# @File : locustClient# @Software: PyCharm# @Desc:from locust import HttpUser, task, between, constant, events, run_single_userclass LocustClient:    @staticmethod    # 示例：动态生成Locust任务    def create_locust_task():        class DynamicUser(HttpUser):            host = "https://x.com"            wait_time = constant(5)            @task            def test_endpoint(self):                response = getattr(self.client, "get")(                    "/perf/",                )                print(response.text)        return DynamicUserif __name__ == '__main__':    # 示例：动态生成Locust任务    user = LocustClient.create_locust_task()    run_single_user(user, 1, )