#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/2/27# @Author : cyq# @File : locustClient# @Software: PyCharmimport jsonfrom json import JSONDecodeErrorfrom dotenv import load_dotenvfrom locust.util.rounding import proper_roundload_dotenv()import osimport timefrom locust.runners import Runnerfrom locust.util.date import format_utc_timestampfrom locust import run_single_userimport geventfrom locust import HttpUser, task, between, FastHttpUser, eventsfrom locust.env import Environmentfrom locust.log import setup_loggingfrom .locustUser import DynamicApiUser,DebuggerUsersetup_logging("INFO", None)def start_locust(interfaceId: str, domain: str, method: str, url: str, request_info: dict, users: int, spawn_rate: int,                 duration: float):    """    启动Locust性能测试。    参数说明：    - interfaceId (str): 接口的唯一标识符。    - domain (str): 目标服务的域名或IP地址。    - method (str): HTTP请求方法（如GET、POST等）。    - url (str): 请求的URL路径。    - request_info (dict): 包含请求相关信息的字典，例如headers、params等。    - users (int): 模拟的并发用户数。    - spawn_rate (int): 每秒启动的用户数（即用户生成速率）。    - duration (float): 测试持续时间，单位为秒。    """    # 初始化DynamicApiUser类，并设置其属性值    DA = DynamicApiUser    DA.api_info = request_info  # 设置请求信息    DA.method = method  # 设置HTTP方法    DA.url = url  # 设置请求URL    # 创建Locust运行环境    env = Environment(user_classes=[DA], events=events, host=domain)    runner = env.create_local_runner()  # 创建本地运行器    env.events.init.fire(environment=env, runner=runner)  # 触发初始化事件    # 启动一个协程用于记录统计数据    gevent.spawn(_my_stats_history, env.runner, interfaceId)    # 开始性能测试    runner.start(users, spawn_rate=spawn_rate)    # 在指定的duration秒后停止测试    gevent.spawn_later(duration, runner.quit)    # 等待所有协程完成    runner.greenlet.join()def _my_stats_history(runner: Runner, interfaceId: str) -> None:    """Save current stats info to history for charts of report."""    print(interfaceId)    # try:        # rc = SyncRedisClient()        # rc.init_pool()        # rc.perf_name = f"perf_{interfaceId}_fhjkdfskj"    # except Exception as e:    #     raise e    while True:        if not runner.stats.total.use_response_times_cache:            break        if runner.state != "ready" and runner.state != "stopped":            _my_update_stats_history(runner)        gevent.sleep(2)def _my_update_stats_history(runner: Runner) -> None:    stats = runner.stats    timestamp = format_utc_timestamp(time.time())    current_response_time_percentiles = {        f"response_time_percentile_{percentile}": [            timestamp,            stats.total.get_current_response_time_percentile(percentile) or 0,        ]        for percentile in [0.5, 0.95]    }    r = {        **current_response_time_percentiles,        "current_rps": [timestamp, stats.total.current_rps or 0],        "current_fail_per_sec": [timestamp, stats.total.current_fail_per_sec or 0],        "total_avg_response_time": [timestamp, proper_round(stats.total.avg_response_time, digits=2)],        "user_count": [timestamp, runner.user_count or 0],        "time": timestamp,    }    print(r)    # rc.perf_l_push(json.dumps(r))    stats.history.append(r)def debug_locust(host: str, method: str, url: str, request_info: dict):    DA = DebuggerUser    DA.api_info = request_info    DA.method = method    DA.url = url    DA.host = host    _env = Environment(user_classes=[DA], events=events, host=host)    run_single_user(DA, _env)if __name__ == '__main__':    host = "https://sit-beijing.cbs.bacic5i5j.com"    info = {'headers': {'Accept': '*/*', 'Cookie': 'sid=5bfb444e-f6f8-4075-b681-e2d6f4d296a0'},            'params': {'_': 1741937242878}, }    # res = start_locust_test(info, 5, 10, "10s")    start_locust("asdads", host, "get", "/perf/performance/checkSpecialFlag", info, 5, 10, 10)