#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/2/14# @Author : cyq# @File : mysqlClient# @Software: PyCharm# @Desc:import pymysqlclass MySqlClient:    def __init__(self, host, user, password, db, port=3306):        self.host = host        self.user = user        self.password = password        self.db = db        self.port = port        self.connection = None    def connect(self):        try:            self.connection = pymysql.connect(                host=self.host,                user=self.user,                password=self.password,                db=self.db,                port=self.port,            )            print("Successfully connected to the database.")        except pymysql.MySQLError as e:            print(f"Error connecting to MySQL Platform: {e}")    def close(self):        if self.connection:            self.connection.close()            print("Database connection closed.")    def execute_query(self, query, params=None):        try:            with self.connection.cursor() as cursor:                cursor.execute(query, params)                self.connection.commit()                return cursor.fetchall()        except pymysql.MySQLError as e:            print(f"Error executing query: {e}")            self.connection.rollback()if __name__ == "__main__":    db_client = MySqlClient(host='127.0.0.1', user='root', password='sdkjfhsdkjhfsdkhfksd', db='autoHub')    db_client.connect()    result = db_client.execute_query("SELECT name FROM interface")    print(result)    db_client.close()