#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/3/14# @Author : cyq# @File : tools# @Software: PyCharm# @Desc:import astfrom typing import Anyfrom urllib.parse import urlparse, ParseResult# from ._myLoguru import MyLogurulog =printclass Tools:    COMMON_MAP = {"true": True, "false": False, "null": None}    @staticmethod    def parse_url(url: str) -> ParseResult:        """        {            "scheme": url_obj.scheme,            "netloc": url_obj.netloc,            "path": url_obj.path,            "params": url_obj.params,            "query": parse_qs(url_obj.query),            "fragment": url_obj.fragment        }        """        url_obj = urlparse(url)        return url_obj    @staticmethod    def str_trans(value: Any):        # 使用 literal_eval 转换字符串为对应的类型        if not isinstance(value, str):            return value        try:            return Tools.COMMON_MAP[value]        except  KeyError:            pass        try:            return ast.literal_eval(value)        except (ValueError, SyntaxError) as e:            # 如果无法转换，返回原始值            log(f"无法转换字符串为对应的类型: {value}")            return valueif __name__ == '__main__':    b = {  "0": "name0",  "1": "name1",  "2": "name2",  "3": "name3",  "4": "name4",  "5": "name5",  "6": "name6",  "7": "name7",  "8": "name8",  "9": "name9"}    a =Tools.str_trans('{ "0": "name0", "1": "name1", "2": "name2", "3": "name3", "4": "name4", "5": "name5", "6": "name6", "7": "name7", "8": "name8", "9": "name9" }')    assert  a == b