#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/3/14# @Author : cyq# @File : tools# @Software: PyCharm# @Desc:from urllib.parse import urlparse, parse_qs, ParseResultclass Tools:    @staticmethod    def parse_url(url: str) -> ParseResult:        """        {            "scheme": url_obj.scheme,            "netloc": url_obj.netloc,            "path": url_obj.path,            "params": url_obj.params,            "query": parse_qs(url_obj.query),            "fragment": url_obj.fragment        }        """        url_obj = urlparse(url)        return url_objif __name__ == '__main__':    a = "http://10.1.1.1:9999//perf/performance/checkSpecialFlag"    print(Tools.parse_url(a))